st.title("Auto Quiz from PDF (Moderate Difficulty)")

st.markdown("Moderate-difficulty MCQs created from your document's key concepts and statements.")

uploaded_file = st.file_uploader("Upload a PDF", type="pdf")

num_questions = st.slider("Number of Questions", min_value=3, max_value=50, value=10)

if uploaded_file is not None and st.button("Generate Quiz"):
    with st.spinner("Processing PDF..."):
        # Extract text
        text = extract_text_from_pdf(uploaded_file)
        if not text or len(text.split()) < 80:
            st.error("Document text appears too short. Use a richer, text-based PDF.")
        else:
            questions = generateModerateQuestions(text, num_questions)
            if not questions:
                st.error("Could not generate questions. Try reducing question count or a different PDF.")
            else:
                st.session_state.questions = questions
                st.success(f"Generated {len(questions)} questions.")

if 'questions' in st.session_state:
    questions = st.session_state.questions
    with st.form("quiz_form"):
        answers = {}
        for i, q in enumerate(questions):
            st.subheader(f"Question {i+1}")
            st.write(q['prompt'])
            options = q['options']
            answers[i] = st.radio(f"Select answer for Q{i+1}", options, key=f"q{i}")

        submitted = st.form_submit_button("Submit Quiz")
        if submitted:
            score = 0
            total = len(questions)
            for i, q in enumerate(questions):
                if answers[i] == q['options'][q['correctIndex']]:
                    score += 1
            st.write(f"Score: {score} / {total}")
            st.balloons()

# PDF text extraction functions (same as flash.py)

def extract_text_from_pdf(filelike) -> str:
    # 1) PyMuPDF
    if HAS_PYMUPDF:
        try:
            with fitz.open(stream=filelike.read(), filetype="pdf") as doc:
                text_parts = []
                for page in doc:
                    txt = page.get_text("text") or ""
                    text_parts.append(txt)
                raw = "\n".join(text_parts)
                if len(raw.strip()) >= 200:
                    return raw
        except Exception:
            pass
        finally:
            try:
                filelike.seek(0)
            except Exception:
                pass

    # 2) PyPDF2
    if HAS_PYPDF2:
        try:
            reader = PyPDF2.PdfReader(filelike)
            if getattr(reader, "is_encrypted", False):
                try:
                    reader.decrypt("")
                except Exception:
                    return ""
            text_parts = []
            for page in reader.pages:
                page_text = page.extract_text() or ""
                text_parts.append(page_text)
            return "\n".join(text_parts)
        except Exception:
            return ""

    return ""

